// Helper function to get question by ID
function getQuestionById(questionId) {
    return RELIGIOUS_DATA.find(q => q.question_id === questionId);
}

// Helper function to get all questions
function getAllQuestions() {
    return RELIGIOUS_DATA;
}

// Helper function to get all religions
function getAllReligions() {
    const religions = new Set();
    RELIGIOUS_DATA.forEach(question => {
        question.religious_perspectives.forEach(perspective => {
            religions.add(perspective.religion);
        });
    });
    return Array.from(religions).sort();
}

// Helper function to get choice label
function getChoiceLabel(choice) {
    const labels = {
        'sa': 'Strongly Agree',
        'a': 'Agree', 
        'd': 'Disagree',
        'sd': 'Strongly Disagree'
    };
    return labels[choice] || choice;
}

// Helper function to get perspective details by religion and question
function getPerspectiveDetails(questionId, religion) {
    const question = getQuestionById(questionId);
    if (!question) return null;
    
    const perspective = question.religious_perspectives.find(p => p.religion === religion);
    if (!perspective) return null;
    
    return {
        stance: getChoiceLabel(perspective.choice),
        choice: perspective.choice,
        religiousText: perspective.religious_text,
        reference: perspective.reference,
        reason: perspective.reason || 'No reasoning provided'
    };
}

// Helper function to format perspective for display
function formatPerspective(perspective) {
    return {
        religion: perspective.religion,
        stance: getChoiceLabel(perspective.choice),
        choice: perspective.choice,
        religiousText: perspective.religious_text,
        reference: perspective.reference,
        reason: perspective.reason || 'No reasoning provided'
    };
}

// Export for use in other scripts
if (typeof module !== 'undefined' && module.exports) {
    module.exports = { 
        RELIGIOUS_DATA, 
        getQuestionById, 
        getAllQuestions, 
        getAllReligions, 
        getChoiceLabel,
        getPerspectiveDetails,
        formatPerspective
    };
}
